<script>
    $(document).ready(function () {
        // Add active class when #from is clicked
        $('#from, #Depart, #Arrival, #Depart2, #Arrival2, #to').on('click', function () {
            // Remove active class from all other elements
            // $('.box.location.from, .box.location.to').removeClass('active');
            // Add active class to the clicked element
            $(this).addClass('active');
            // Toggle display of the flight suggestions
            $(this).find('.flight_from_suggestionss').toggle();
        });

        // Add click event listener to the airport options using event delegation
        $('.airfrom_listt').on('click', '.hotel_features', function (e) {
            // Get airport details
            var airportName = $(this).find('h4').text();
            var airportSubValue = $(this).find('p').text();

            // Update values and subvalues within the same box
            $(this).closest('.box').find('.values').text(airportName);
            $(this).closest('.box').find('.subvalues').text(airportSubValue);

            // Hide suggestions and remove active class from the box
            $(this).closest('.box').removeClass('active');
            $(this).closest('.flight_from_suggestionss').hide();

            // Prevent click event from propagating to parent elements
            e.stopPropagation();
        });

        // Add click event listener to input elements to remove active class
        $('.input_text input').on('click', function (e) {
            // Prevent click event from propagating to parent elements
            e.stopPropagation();
            // Remove active class from the box
            $(this).closest('.box').removeClass('active');
        });

        // Hide suggestions when clicking outside the box
        $(document).on('click', function (e) {
            if (!$(e.target).closest('.box.location.from').length) {
                $('.flight_from_suggestionss').hide();
                $('.box.location.from').removeClass('active');
            }
        });
    });

</script>
