<script>
    $(document).ready(function () {
        // Show the location search input when the main tourBox is clicked
        $('#tourBox').on('click', function () {
            $(this).find('.locationsearchh').removeClass('d-none');
        });

        // Filter the location list as the user types
        $('#tourBox .input_text input').on('input', function () {
            var searchTerm = $(this).val().toLowerCase();
            $('#tourBox .location_listtt .locations').each(function () {
                var locationName = $(this).find('.name').text().toLowerCase();
                if (locationName.includes(searchTerm)) {
                    $(this).show();
                } else {
                    $(this).hide();
                }
            });
        });

        // Update the main value and subvalue when a location is clicked
        $('#tourBox').on('click', '.locations', function (e) {
            e.stopPropagation();
            var selectedLocation = $(this).find('.name').text();
            $("[name='destination']").val(selectedLocation);
            $('#tourBox .value').text(selectedLocation);
            $('#tourBox .subvalue').text('Selected Location');
            $('#tourBox .locationsearchh').addClass('d-none');
        });

        // Prevent event propagation to avoid immediate hiding of locationsearchh
        $('#tourBox .input_text input').on('click', function (e) {
            e.stopPropagation();
        });

        // Hide locationsearchh when clicking outside the tourBox
        $(document).on('click', function (e) {
            if (!$(e.target).closest('#tourBox').length) {
                $('#tourBox .locationsearchh').addClass('d-none');
            }
        });
    });
</script>


