<!-- price range filter  -->
<script>
document.addEventListener('DOMContentLoaded', function () {
    const rangeMin = document.getElementById('rangeMin');
    const rangeMax = document.getElementById('rangeMax');
    const minAmount = document.getElementById('minAmount');
    const maxAmount = document.getElementById('maxAmount');

    function updateValues() {
        let minValue = parseInt(rangeMin.value);
        let maxValue = parseInt(rangeMax.value);

        // Ensure that minValue is not greater than maxValue - 10
        if (minValue > maxValue - 10) {
            minValue = maxValue - 10;
            rangeMin.value = minValue;
        }

        // Ensure that maxValue is not less than minValue + 10
        if (maxValue < minValue + 10) {
            maxValue = minValue + 10;
            rangeMax.value = maxValue;
        }

        minAmount.textContent = `PKR ${minValue.toLocaleString()}`;
        maxAmount.textContent = `PKR ${maxValue.toLocaleString()}`;
    }

    rangeMin.addEventListener('input', updateValues);
    rangeMax.addEventListener('input', updateValues);

    // Initial values setup
    updateValues();
});

</script>



