<script>

    function number_format (number, decimals, dec_point, thousands_sep) {
        // Strip all characters but numerical ones.
        number = (number + '').replace(/[^0-9+\-Ee.]/g, '');
        var n = !isFinite(+number) ? 0 : +number,
            prec = !isFinite(+decimals) ? 0 : Math.abs(decimals),
            sep = (typeof thousands_sep === 'undefined') ? ',' : thousands_sep,
            dec = (typeof dec_point === 'undefined') ? '.' : dec_point,
            s = '',
            toFixedFix = function (n, prec) {
                var k = Math.pow(10, prec);
                return '' + Math.round(n * k) / k;
            };
        // Fix for IE parseFloat(0.55).toFixed(0) = 0;
        s = (prec ? toFixedFix(n, prec) : '' + Math.round(n)).split('.');
        if (s[0].length > 3) {
            s[0] = s[0].replace(/\B(?=(?:\d{3})+(?!\d))/g, sep);
        }
        if ((s[1] || '').length < prec) {
            s[1] = s[1] || '';
            s[1] += new Array(prec - s[1].length + 1).join('0');
        }
        return s.join(dec);
    }

</script>

<script>

document.querySelectorAll('.custom-nav ul > li > a').forEach((anchor) => {
  anchor.addEventListener('click', (event) => {
    event.preventDefault();
    const accordionId = anchor.getAttribute('href').replace('#', '');
    const accordionItem = document.querySelector(`#${accordionId}`);
    const navLinks = document.querySelectorAll('.custom-nav ul > li > a');
    const currentActiveLink = document.querySelector('.custom-nav ul > li > a.show');
    const currentActiveAccordion = document.querySelector('.accordion-item.show');

    // Remove show class from previous accordion item
    if (currentActiveAccordion) {
      currentActiveAccordion.classList.remove('show');
    }

    // Toggle show class on navigation items
    if (currentActiveLink) {
      currentActiveLink.classList.remove('show');
    }
    anchor.classList.add('show');

    // Toggle show class on accordion items
    accordionItem.classList.add('show');

    // Scroll to the top of the accordion item
    accordionItem.scrollIntoView({ behavior: 'smooth' });
  });
});
  </script>

