<script>
    document.addEventListener("DOMContentLoaded", () => {
        const starIcons = document.querySelectorAll(".star");
        const radioInputs = document.querySelectorAll(".rating input[type='radio']");

        starIcons.forEach((icon, index) => {
            icon.addEventListener("click", () => {
                // Set the corresponding radio input to checked
                radioInputs[index].checked = true;

                // Toggle active class for all stars up to the clicked one
                starIcons.forEach((star, idx) => {
                    star.classList.toggle("active", idx <= index);
                });
            });
        });
    });

</script>

